from odoo import _, api, fields, models
from datetime import datetime

class AssesmenAwalRawatJalan(models.Model):
    _name           = 'cdn.asesmen.awal.rawat.jalan'
    _description    = 'Asesmen Awal Rawat Jalan'
    _inherits       = {
        'cdn.erm.base': 'rm_base_id',
        }
    _inherit        = [
        'mail.thread', 
        'mail.activity.mixin',
        'cdn.erm.mixin',
        'cdn.report.mailmerge',
        'cdn.simrs.library'
        ]
    
    rm_base_id      = fields.Many2one(comodel_name='cdn.erm.base', string='RM', required=True, ondelete='cascade')

    erm_properties      = fields.Properties(
        definition="rm_id.erm_properties_definition",
        string="Properties",
    )

    
    # ============================================================
    # 1. KELUHAN UTAMA (PENGKAJIAN KEPERAWATAN)
    # ============================================================
    riwayat_obat                = fields.Text(string='Riwayat Obat', tracking=True)

    # ============================================================
    # 2. PEMERIKSAAN FISIK
    # ============================================================
    # Keadaan Umum
    keadaan_umum        = fields.Selection([
        ('baik', 'Baik'),
        ('pucat', 'Pucat'),
        ('lemah', 'Lemah'),
        ('lainnya', 'Lainnya')
    ], string='Keadaan Umum', tracking=True)
    keadaan_umum_lainnya = fields.Char(string='Keadaan Umum Lainnya', tracking=True)
    penampilan          = fields.Text(string='Penampilan', tracking=True)

    # Tanda-tanda Vital
    # kesadaran           = fields.Char(string='Kesadaran', tracking=True)
    # tekanan_darah       = fields.Char(string='Tekanan Darah (mmHg)', tracking=True)
    # saturasi_oksigen    = fields.Float(string='Saturasi Oksigen (%)', digits=(5, 2), tracking=True)
    # nadi                = fields.Integer(string='Nadi (x/menit)', tracking=True)
    # berat_badan         = fields.Float(string='Berat Badan (Kg)', digits=(5, 2), tracking=True)
    # respiratory_rate    = fields.Integer(string='RR (x/menit)', tracking=True)
    # tinggi_badan        = fields.Integer(string='Tinggi Badan (cm)', tracking=True)
    # suhu                = fields.Float(string='Suhu (Â°C)', digits=(4, 2), tracking=True)

    # ============================================================
    # 3. PSIKOSOSIAL DAN EKONOMI
    # ============================================================
    # Status Sosial
    tinggal_bersama     = fields.Selection([
        ('suami_istri', 'Suami/Istri'),
        ('orang_tua', 'Orang Tua'),
        ('sendiri', 'Sendiri'),
        ('lainnya', 'Lainnya')
    ], string='Tinggal Bersama', tracking=True)
    tinggal_bersama_lainnya = fields.Char(string='Tinggal Bersama Lainnya', tracking=True)

    # Status Spiritual
    kegiatan_ibadah     = fields.Selection([
        ('ya', 'Ya'),
        ('tidak', 'Tidak')
    ], string='Kegiatan Ibadah', tracking=True)
    keyakinan_mitologi  = fields.Selection([
        ('ya', 'Ya'),
        ('tidak_ada', 'Tidak Ada')
    ], string='Keyakinan Mitologi/Budaya Setempat', tracking=True)

    # Pekerjaan
    pekerjaan           = fields.Selection([
        ('wiraswasta', 'Wiraswasta'),
        ('pegawai_swasta', 'Pegawai Swasta'),
        ('pegawai_negeri', 'Pegawai Negeri'),
        ('lainnya', 'Lainnya')
    ], string='Pekerjaan', tracking=True)
    pekerjaan_lainnya   = fields.Char(string='Pekerjaan Lainnya', tracking=True)

    # Status Psikologi
    status_psikologi    = fields.Selection([
        ('tenang', 'Tenang'),
        ('cemas', 'Cemas'),
        ('bingung', 'Bingung'),
        ('marah', 'Marah'),
        ('depresi', 'Depresi')
    ], string='Status Psikologi', tracking=True)

    # ============================================================
    # 4. ASESMEN RESIKO JATUH
    # ============================================================
    cara_berjalan_normal    = fields.Boolean(string='Cara Berjalan Normal/Tidak Seimbang', tracking=True)
    menggunakan_alat_bantu  = fields.Boolean(string='Menggunakan Alat Bantu (Meja/Kursi/Tongkat)', tracking=True)
    
    # Hasil Risiko (Computed)
    hasil_risiko_jatuh      = fields.Selection([
        ('tidak_berisiko', 'Tidak Berisiko'),
        ('risiko_rendah', 'Risiko Rendah'),
        ('risiko_tinggi', 'Risiko Tinggi')
    ], string='Hasil Risiko Jatuh', compute='_compute_hasil_risiko_jatuh', store=True, tracking=True)

    # ============================================================
    # 5. ASESMEN NYERI
    # ============================================================
    pasien_merasa_nyeri     = fields.Selection([
        ('ya', 'Ya'),
        ('tidak', 'Tidak')
    ], string='Pasien Merasa Nyeri', tracking=True)
    mengganggu_aktivitas    = fields.Selection([
        ('ya', 'Ya'),
        ('tidak', 'Tidak')
    ], string='Mengganggu Aktivitas', tracking=True)
    severity_scale          = fields.Selection([
        ('0', '0 - No Pain'),
        ('1', '1-3 - Mild'),
        ('2', '4-6 - Moderate'),
        ('3', '7-9 - Severe'),
        ('4', '10 - Very Severe')
    ], string='Severity & Scale', tracking=True)


    def _get_default_penandaan_bg_img(self):
        return """"""
    severity_scale_img      = fields.Binary(string='Severity & Scale Image', default=_get_default_penandaan_bg_img)
    # Tanda Tangan Perawat
    # tanda_tangan_perawat    = fields.Char(string='Tanda Tangan Perawat')
    # nama_terang_perawat     = fields.Char(string='Nama Terang Perawat', tracking=True)

    # ============================================================
    # COMPUTED FIELDS
    # ============================================================
    @api.depends('cara_berjalan_normal', 'menggunakan_alat_bantu')
    def _compute_hasil_risiko_jatuh(self):
        for record in self:
            if record.cara_berjalan_normal and record.menggunakan_alat_bantu:
                record.hasil_risiko_jatuh = 'risiko_tinggi'
            elif record.cara_berjalan_normal or record.menggunakan_alat_bantu:
                record.hasil_risiko_jatuh = 'risiko_rendah'
            else:
                record.hasil_risiko_jatuh = 'tidak_berisiko'

    # ============================================================
    # PENANDAAN
    # ============================================================
    @api.model
    def _get_default_penandaan_bg_img(self):
        return self.env['cdn.erm.daftar.line'].sudo()._get_image_code('cdn_simrs_rekamedis_add.asesmen_awal_rawat_jalan_bg')
    
    penandaan_bg_img        = fields.Binary(string='Penandaan BG', default=_get_default_penandaan_bg_img)
    penandaan_json_img      = fields.Text(string='Penandaan JSON')
    penandaan_out_img       = fields.Image(string='Penandaan OUT')
    deskripsi_penandaan     = fields.Text(string='Deskripsi Penandaan')

    # ============================================================
    # ACTIONS
    # ============================================================
    def action_penandaan(self):
        model_id = self.env['ir.model'].search([('model', '=', self._name)])
        data = {
            'default_bg_name'             : 'penandaan_bg_img',
            'default_bg_json_name'        : 'penandaan_json_img',
            'default_bg_out_name'         : 'penandaan_out_img',
            'default_model_id'            : model_id.id,
            'default_data_id'             : self.id,
            'default_penandaan_bg_img'    : self.penandaan_bg_img,
            'default_penandaan_json_img'  : self.penandaan_json_img,
            'default_penandaan_out_img'   : self.penandaan_out_img,
            'default_height'              : '100',
            'default_width'               : '100',
        }
        return self.rm_base_id.open_wizard_canvas(data)

    # ============================================================
    # REPORT PDF
    # ============================================================
    def action_print(self):
        return {
            'type'  : 'ir.actions.act_url',
            'url'   : f'/cdn_print_report_pdf/cdn.asesmen.awal.rawat.jalan/{self.id}/_generate_print_report',
            'target': 'new',
        }
    # keluhan_utama          = fields.Text(string='Keluhan Utama')

    def _generate_print_report(self):
        # tanggal_kunjungan = 
        data_field = {
            'no_rm'             : self.pasien_id.no_rm or '',
            'nama_pasien'       : self.pasien_id.name or '',
            'tgl_lahir'         : self.pasien_id.tanggal_lahir.strftime('%d-%m-%Y') if self.pasien_id.tanggal_lahir else '',
            'tgl_kunjungan'     : f'{self._to_wib(self.tanggal).strftime('%d-%m-%Y')}' if self.tanggal else '',
            'jam_kunjungan'     : f'{self._to_wib(self.tanggal).strftime('%H:%M')}' if self.tanggal else '',
            'usia'             : self.pasien_id.usia or '',
            
            
            'keluhan_utama'     : self.keluhan_utama or '',
            'riwayat_penyakit'  : self.riwayat_dulu or '',
            'riwayat_obat'      : self.riwayat_obat or '',
            'riwayat_alergi'    : self.riwayat_alergi or '',
            'tinggal_bersama'   : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','tinggal_bersama',self.tinggal_bersama),
            'pekerjaan'         : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','pekerjaan',self.pekerjaan),
            'status_psikologi'  : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','status_psikologi',self.status_psikologi),
            'resiko_jatuh_cara_jalan'   : 'Ya' if self.cara_berjalan_normal else 'Tidak',
            'resiko_jatuh_pegang_benda' : 'Ya' if self.menggunakan_alat_bantu else 'Tidak',
            'hasil_risiko_jatuh'        : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','hasil_risiko_jatuh',self.hasil_risiko_jatuh),
            'pasien_merasa_nyeri'       : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','pasien_merasa_nyeri',self.pasien_merasa_nyeri),
            'severity_scale'            : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','severity_scale',self.severity_scale),
            'mengganggu_aktivitas'      : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','mengganggu_aktivitas',self.mengganggu_aktivitas),
            'saturasi_oksigen'          : f'{self.spo2} %' if self.spo2 else '',
            'kesadaran'                 : self._get_selection_value('cdn.asesmen.awal.rawat.jalan','tingkat_kesadaran',self.tingkat_kesadaran),
            'bb_kg'                     : f'{self.weight} kg' if self.weight else '',
            'tb_cm'                     : f'{self.height} cm' if self.height else '',
            'nadi'                      : f'{self.hr}' if self.hr else '',
            'rr'                        : f'{self.rr}' if self.rr else '',
            'suhu'                      : f'{self.temp} Â°C' if self.temp else '',
            'tekanan_darah'             : f'{self.systolic_bp}/{self.diastolic_bp} mmHg' if self.systolic_bp and self.diastolic_bp else '',
            'penampilan_ciri_khusus'    : self.penampilan or 'Penampilan (Ciri Khusus, misal: tato, tanda, lahir, dll)',
            'perawat_nama_tanda_tangan' : self.qr_ttd_perawat_partner_id.name or '...................................',
        }
        
        data_image = [
            {'key'       : '{{logo}}','value'     : self.company_id.logo,'inches'    : 0.7,},
            {'key'       : '{{ttd_perawat}}','value'     : self.qr_ttd_perawat_code,'inches'    : 1,},
            ]
        
        template = 'cdn_simrs_rekamedis_add/template/klinik_mata/assesmen_awal_rawat_jalan.docx'
        return self._mail_merge_to_pdf(
            path        = template, 
            data_info   = data_field, 
            image_info  = data_image
        )